//////////////////////////////////////////////////////////////////////////////// ВЫБРАТЬ ЕСТЬNULL(AR.Client, ВТ_Остатки.Client) КАК Client, ЕСТЬNULL(AR.Company, ВТ_Остатки.Company) КАК Company, ЕСТЬNULL(AR.Source, ВТ_Остатки.Source) КАК Source, ЕСТЬNULL(AR.Location, ВТ_Остатки.Location) КАК Location, ЕСТЬNULL(AR.SubSubSegment, ВТ_Остатки.SubSubSegment) КАК SubSubSegment, ЕСТЬNULL(AR.TransType, ВТ_Остатки.TransType) КАК TransType, ЕСТЬNULL(AR.НомерДокумента, ВТ_Остатки.НомерДокумента) КАК Invoice, ЕСТЬNULL(AR.Account, ВТ_Остатки.Account) КАК Account, ЕСТЬNULL(AR.Currency, ВТ_Остатки.Currency) КАК Currency, ЕСТЬNULL(AR.AU, ВТ_Остатки.AU) КАК AU, ЕСТЬNULL(AR.Amount, 0) КАК AR_Amount, ЕСТЬNULL(ВТ_Остатки.AmountОстаток, 0) КАК ERM_Amount ИЗ ВТ_Остатки КАК ВТ_Остатки ПОЛНОЕ СОЕДИНЕНИЕ AR КАК AR ПО ВТ_Остатки.Client = AR.Client И ВТ_Остатки.Company = AR.Company И ВТ_Остатки.Source = AR.Source И ВТ_Остатки.Location = AR.Location И ВТ_Остатки.SubSubSegment = AR.SubSubSegment И ВТ_Остатки.Invoice = AR.Invoice И ВТ_Остатки.Account = AR.Account И ВТ_Остатки.Currency = AR.Currency И ВТ_Остатки.AU = AR.AU ГДЕ ЕСТЬNULL(AR.Amount, 0) <> ЕСТЬNULL(ВТ_Остатки.AmountОстаток, 0)
